/*<a target="_self" href="javascript:bookmark()">加入收藏</a>-*/
function bookmark(){
var title=document.title
var url=document.location.href
if (window.sidebar)
	window.sidebar.addPanel(title, url,""); 
else if( window.opera && window.print ){ 
	var mbm = document.createElement('a'); 
	mbm.setAttribute('rel','sidebar'); 
	mbm.setAttribute('href',url); 
	mbm.setAttribute('title',title); 
	mbm.click();} 
else if( document.all )
	window.external.AddFavorite( url, title);
else alert("请按ctrl+d来手工添加！"); 
}

/*<a target="_self" onclick="setHomepage('http://www.google.com/');" href="javascript:;">设为首页</a>*/
function setHomepage(sURL) {
	if(BROWSER.ie){
		document.body.style.behavior = 'url(#default#homepage)';
		document.body.setHomePage(sURL);
	} else {
		alert("非 IE 浏览器请手动将本站设为首页！");
	}
}
$(function(){
  //高亮当前选项(停留)
  var JS_mo_menu = $('.JS_mo_menu li');
  JS_mo_menu.mouseover(function(){
  $(this).addClass('hover')
      .siblings().removeClass('hover');
  });
  //高亮当前选项(非停留)	
	var JS_mo_hover = $('.JS_mo_hover li');
  JS_mo_hover.mouseover(function(){
  	$(this).addClass('selected') 
	});
	JS_mo_hover.mouseout(function(){
  	$(this).removeClass('selected') 
	});
  //切换样式(默认mouseover触发)
  var tab_nav_li = $('.JS_tab_nav li');
  tab_nav_li.mouseover(function(){
    $(this).addClass('selected')
  			.siblings().removeClass('selected');
  var tab_nav_li_index = tab_nav_li.index(this);
  $('.tab_switch .tabcon').eq(tab_nav_li_index).show()
  			.siblings().hide();
  }).hover(function(){
    $(this).addClass('hover');
  },function(){
    $(this).removeClass('hover');	
  });
  //切换加滚动样式，注意必不可少.scoll_box{position:relative}属性，不然IE6、7会出问题
  var scoll_nav_li = $('.scoll_nav li');
  scoll_nav_li.mouseover(function(){
  	$(this).addClass('selected')
  			 .siblings().removeClass('selected');
  var scoll_nav_li_index = scoll_nav_li.index(this);   
  $('.scoll_switch .scoll_box').eq(scoll_nav_li_index).addClass('selected')
  				 .siblings().removeClass('selected');
  });
  $("#JS_promo img").banner_thaw({
		thumbObj: "#JS_promoTriggers li",
		thumbNowClass: "current",
		botPrev:"span.prev",
		botNext:"span.next",
		changeTime: 3000
	});
	
  $(".tabcon1 .scoll").jCarouselLite({//滚动控制
  	btnNext: ".tabcon1 .next",
  	btnPrev: ".tabcon1 .prev",
  	visible: 5,
  	scroll: 1,
  	auto: 1200,
  	speed: 500
  });
});
/* 最后一个元素添加特定样式 */
$(".last_child").each(function(){
	$(this).children().last().addClass("last");
})
/* 去除虚线(部分元素) */
$(".JS_no_focus a").bind("focus",function(){
	if(this.blur){this.blur()};//常用于幻灯的数字按钮
});
/* 打印页面 */
$(".print").click(function(){
	window.print();return false;
});
